BEGIN TRANSACTION;
CREATE TABLE `bushimo_item_m` (
    `bushimo_item_id`  INTEGER NOT NULL,
    `bushimo_item_asset` TEXT,
    `bushimo_item_type`  INTEGER NOT NULL,
    `bushimo_item_point_id` TEXT NOT NULL,
    `sandbox_bushimo_item_point_id` TEXT NOT NULL,
    `bushimo_item_name` TEXT,
    PRIMARY KEY (`bushimo_item_id`)
);
CREATE TABLE `bushimo_reward_m` (
    `bushimo_reward_id`  INTEGER NOT NULL,
    `reward_type_code`  INTEGER NOT NULL,
    `reward_type_id`  INTEGER,
    `reward_type_value`  INTEGER,
    `bushimo_item_id`  INTEGER NOT NULL,
    `amount`  INTEGER NOT NULL,
    PRIMARY KEY (`bushimo_reward_id`)
);
COMMIT;
